import React, { useState } from 'react';
import EventCard from './EventCard';
import './App.css';

function App() {
  const questions = [
    "Start",
    (
      <div>
        <p>Question 1</p>
        <p>What flower is this?</p>
        <img src="data:image/jpeg;base64," alt="" 
        style={{ maxWidth: '100%', maxHeight: '100%' }}/>
      </div>
    ),
    (
      <div>
        <p>Question 2</p>
        What is the process by which plants make their own food using sunlight?
        </div>
    
    ),
    (
      <div>
        <p>Question 3</p>
        Which gas do plants absorb from the atmosphere during photosynthesis?
        </div>
    
    ),
    (
      <div>
        <p>Question 4</p>
        What part of a plant absorbs water and minerals from the soil?
        </div>
    
    ),
    (
      <div>
        <p>Question 5</p>
        Which plant is known as the "King of Spices" and is commonly used to flavor dishes?
        </div>
    
    ),
    (
      <div>
        <p>Question 6</p>
        What is the primary function of leaves in a plant?
        </div>
    
    ),
    (
      <div>
        <p>Question 7</p>
        Which famous plant is known for its soothing gel used to treat sunburns and skin irritations?
        </div>
    
    ),
    (
      <div>
        <p>Question 8</p>
        What is the reproductive part of a flower that contains the pollen?
        </div>
    
    ),
    (
      <div>
        <p>Question 9</p>
        Which plant is often associated with Halloween, known for its orange color and carving tradition?
        </div>
    
    ),
    (
      <div>
        <p>Question 10</p>
        What is the tallest tree species on Earth, found in California and Oregon, USA?
        </div>
    
    ),
    // Add more questions here
  ];

  const answers = [
    "Click the buttons to begin",
    "Answer 1: Lily",
    "Answer 2: Photosynthesis",
    "Answer 3: Carbon dioxide (CO2)",
    "Answer 4: Roots",
    "Answer 5: Black Pepper",
    "Answer 6: Photosynthesis (for producing food)",
    "Answer 7: Aloe Vera",
    "Answer 8: Stamen",
    "Answer 9: Pumpkin",
    "Answer 10: Coast Redwood",
    // Add more answers here in the same order as questions
  ];

  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);

  const nextCard = () => {
    setShowAnswer(false); // Reset to question view when moving to the next card

    if (currentCardIndex === 0) {
      // If the current card is "Start," move directly to "Question 1"
      setCurrentCardIndex(1);
    } else if (currentCardIndex === questions.length - 1) {
      // If it's the last question, loop back to "Question 1"
      setCurrentCardIndex(1);
    } else {
      setCurrentCardIndex((prevIndex) => prevIndex + 1);
    }
  };

  const toggleCardView = () => {
    setShowAnswer((prevShowAnswer) => !prevShowAnswer);
  };

  return (
    <div className="App">
      <h1 style={{ color: 'black' }}>Flashcards</h1>
      <h2 style={{ color: 'black' }}>Quiz about plants</h2>
      <div className="total-questions" >
        Total Questions: {questions.length - 1}
      </div>
      <div className="card-container">
        <EventCard
          question={questions[currentCardIndex]}
          answer={answers[currentCardIndex]}
          showAnswer={showAnswer}
          onCardClick={toggleCardView}
        />

        <div className="button-container">
          <button onClick={nextCard}>&rarr;</button>
        </div>
      </div>
    </div>
  );
}

export default App;
